import React from "react";
import { useLocation } from "react-router-dom";
import { Container, Typography, Grid, Box } from "@mui/material";

const Home = () => {
    const location = useLocation();
    const user = location.state?.user;

    const calculateCalorieMessage = (healthGoal) => {
        if (healthGoal.toLowerCase().includes("weight loss")) {
            return "You need to burn more calories today!";
        } else if (healthGoal.toLowerCase().includes("gain weight")) {
            return "You need to consume more calories today!";
        } else {
            return "Keep maintaining your current calorie intake!";
        }
    };

    const calorieMessage = user ? calculateCalorieMessage(user.health_goals) : "";

    return (
        <Box
            sx={{
                backgroundColor: "#f5f5f5", // Light gray background
                minHeight: "100vh",
                padding: "2rem",
            }}
        >
            <Container>
                <Typography variant="h3" align="center" gutterBottom>
                    Welcome, {user ? user.username : "Guest"}!
                </Typography>
                {user && (
                    <Typography
                        variant="body1"
                        align="center"
                        gutterBottom
                        sx={{ color: "green", fontWeight: "bold" }}
                    >
                        {calorieMessage}
                    </Typography>
                )}
                <Typography variant="body1" align="center" gutterBottom>
                    Track your nutritional intake and improve your health.
                </Typography>
                <Grid container spacing={4} sx={{ mt: 3 }}>
                    <Grid item xs={12} md={6}>
                        <img
                            src="/AvocadoToastwithEgg.jpeg"
                            alt="Healthy Food"
                            style={{
                                width: "100%",
                                borderRadius: "10px",
                                boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
                            }}
                        />
                        <Typography
                            variant="h6"
                            align="center"
                            sx={{ mt: 2, fontWeight: "bold" }}
                        >
                            Healthy Food
                        </Typography>
                    </Grid>
                    <Grid item xs={12} md={6}>
                        <img
                            src="/analysis.jpg"
                            alt="Nutritional Analysis"
                            style={{
                                width: "100%",
                                height: "375px",
                                borderRadius: "10px",
                                boxShadow: "0 4px 8px rgba(0, 0, 0, 0.1)",
                            }}
                        />
                        <Typography
                            variant="h6"
                            align="center"
                            sx={{ mt: 2, fontWeight: "bold" }}
                        >
                            Nutritional Analysis
                        </Typography>
                    </Grid>
                </Grid>
            </Container>
        </Box>
    );
};

export default Home;
