import React, { useState, useEffect } from "react";
import { Container, Typography, Box, CircularProgress } from "@mui/material";

const TrackNutrients = () => {
  const [nutrientData, setNutrientData] = useState(null);
  const [loading, setLoading] = useState(true);

  useEffect(() => {
    // Fetch nutrient data from the backend
    fetch("http://localhost:5000/api/nutrients")
      .then((response) => response.json())
      .then((data) => {
        setNutrientData(data);
        setLoading(false);
      })
      .catch((error) => {
        console.error("Error fetching nutrient data:", error);
        setLoading(false);
      });
  }, []);

  return (
    <Box
      sx={{
        backgroundColor: "#f5f5f5",
        minHeight: "100vh",
        padding: "2rem",
      }}
    >
      <Container>
        <Typography variant="h3" align="center" gutterBottom>
          Track Your Nutrients
        </Typography>
        <Typography variant="body1" align="center" gutterBottom>
          Input your daily food intake and get detailed reports on macronutrients and micronutrients.
        </Typography>
        {loading ? (
          <Box sx={{ display: "flex", justifyContent: "center", mt: 4 }}>
            <CircularProgress />
          </Box>
        ) : (
          <Box sx={{ mt: 4 }}>
            {nutrientData ? (
              <>
                <Typography variant="h5" gutterBottom>
                  Macronutrients
                </Typography>
                <Typography variant="body1">
                  Carbohydrates: {nutrientData.macronutrients.carbohydrates}
                </Typography>
                <Typography variant="body1">
                  Proteins: {nutrientData.macronutrients.proteins}
                </Typography>
                <Typography variant="body1">
                  Fats: {nutrientData.macronutrients.fats}
                </Typography>
                <Typography variant="h5" gutterBottom sx={{ mt: 3 }}>
                  Micronutrients
                </Typography>
                <Typography variant="body1">
                  Vitamin A: {nutrientData.micronutrients.vitamins.vitaminA}
                </Typography>
                <Typography variant="body1">
                  Vitamin C: {nutrientData.micronutrients.vitamins.vitaminC}
                </Typography>
                <Typography variant="body1">
                  Vitamin D: {nutrientData.micronutrients.vitamins.vitaminD}
                </Typography>
                <Typography variant="body1">
                  Calcium: {nutrientData.micronutrients.minerals.calcium}
                </Typography>
                <Typography variant="body1">
                  Iron: {nutrientData.micronutrients.minerals.iron}
                </Typography>
                <Typography variant="body1">
                  Potassium: {nutrientData.micronutrients.minerals.potassium}
                </Typography>
              </>
            ) : (
              <Typography variant="body1" color="error">
                Failed to fetch data. Please try again later.
              </Typography>
            )}
          </Box>

        )}
      </Container>
    </Box>
  );
};

export default TrackNutrients;
