import React, { useEffect, useState } from "react";
import axios from "axios";

const Suggestions = ({ user }) => {
  const [suggestions, setSuggestions] = useState({
    food: [],
    exercises: [],
  });
  const [error, setError] = useState("");

  useEffect(() => {
    const fetchSuggestions = async () => {
      if (!user || !user.id) {
        setError("User details are missing. Please log in again.");
        return;
      }

      try {
        const response = await axios.get("http://localhost:5000/api/suggestions", {
          params: { user_id: user.id },
        });
        if (response.status === 200) {
          setSuggestions({
            food: response.data.food_suggestions || [],
            exercises: response.data.exercise_suggestions || [],
          });
          setError("");
        }
      } catch (err) {
        setError("Failed to fetch suggestions. Please try again later.");
        console.error(err);
      }
    };

    fetchSuggestions();
  }, [user]);

  return (
    <div style={{ padding: "2rem" }}>
      <h2>Suggestions</h2>
      <p>Personalized nutritional recommendations based on your details.</p>
      {error ? (
        <p style={{ color: "red" }}>{error}</p>
      ) : (
        <>
          <h3>Food Suggestions</h3>
          <ul>
            {suggestions.food.length > 0
              ? suggestions.food.map((item, index) => <li key={index}>{item}</li>)
              : "No food suggestions available."}
          </ul>
          <h3>Exercise Suggestions</h3>
          <ul>
            {suggestions.exercises.length > 0
              ? suggestions.exercises.map((item, index) => <li key={index}>{item}</li>)
              : "No exercise suggestions available."}
          </ul>
        </>
      )}
    </div>
  );
};

export default Suggestions;
